import Foundation

// 1926. Nearest Exit from Entrance in Maze
// https://leetcode.com/problems/nearest-exit-from-entrance-in-maze/
// 需要注意的是，添加完满足条件的位置之后，就要将这个位置设置为已遍历。否则会出现重复元素。
class Leetcode1926 {
  
  struct Position {
    let row: Int
    let column: Int
  }
  
  func isExit(_ position: Position, _ maze: [[Character]]) -> Bool {
    return position.row == 0 || position.row == maze.count - 1 || position.column == 0 || position.column == maze[0].count - 1
  }
  
  func nearestExit(_ maze: [[Character]], _ entrance: [Int]) -> Int {
    let entrance = Position(row: entrance[0], column: entrance[1])
    var maze = maze
    var queue: [Position] = [entrance]
    maze[entrance.row][entrance.column] = Character("+")
    
    var depth = 0
    while !queue.isEmpty {
      var temp: [Position] = []
      for top in queue {
        if depth > 0 && isExit(top, maze) {
          return depth
        }
        if (top.row - 1) >= 0 && maze[top.row - 1][top.column] == Character(".") {
          temp.append(Position(row: top.row - 1, column: top.column))
          maze[top.row - 1][top.column] = Character("+")
        }
        if (top.row + 1) < maze.count && maze[top.row + 1][top.column] == Character(".") {
          temp.append(Position(row: top.row + 1, column: top.column))
          maze[top.row + 1][top.column] = Character("+")
        }
        if (top.column - 1) >= 0 && maze[top.row][top.column - 1] == Character(".") {
          temp.append(Position(row: top.row, column: top.column - 1))
          maze[top.row][top.column - 1] = Character("+")
        }
        if (top.column + 1) < maze[0].count && maze[top.row][top.column + 1] == Character(".") {
          temp.append(Position(row: top.row, column: top.column + 1))
          maze[top.row][top.column + 1] = Character("+")
        }
      }
      queue = temp
      depth += 1
    }
    return -1
  }
  
